import numpy as npimport argparseimport six.moves.cPickle as picklefrom chainer import cudaimport subprocessparser = argparse.ArgumentParser()parser.add_argument('--dir', '-d', help='Target file path')args = parser.parse_args()with open(args.dir, "rb") as f:    loss = [cuda.to_cpu(_f) for _f in pickle.load(f)]step = np.arange(0, len(loss), 1)gnuplot = subprocess.Popen(["/usr/local/bin/gnuplot"],                           stdin=subprocess.PIPE)gnuplot.stdin.write(("set term dumb 120 30\n").encode("utf-8"))gnuplot.stdin.write(("plot '-' using 1:2 title 'Line1' with linespoints \n").encode("utf-8"))for i,j in zip(step, loss):   gnuplot.stdin.write(("%f %f\n" % (i,j)).encode("utf-8"))gnuplot.stdin.write(("e\n").encode("utf-8"))gnuplot.stdin.flush()